import React, { useState } from 'react';


function ASalary() {
  const [employees, setEmployees] = useState([
    { id: 1, name: 'John Doe', department: 'Engineering', salary: '$6,000' },
    { id: 2, name: 'Grace Mensah', department: 'Sales', salary: '$5,500' },
    { id: 3, name: 'Samuel Opoku', department: 'Human Resources', salary: '$6,200' },
  ]);

  const handleDelete = (id) => {
    setEmployees(employees.filter((employee) => employee.id !== id));
  };

  const handleEdit = (id) => {
    // Logic for editing an employee's salary (could open a modal or form for editing)
    alert(`Editing employee with id: ${id}`);
  };

  return (
    <div className="salaries-container">
      <h2>Salaries</h2>
      <button className="add-employee-btn">+ Add Employee</button>
      <table className="salaries-table">
        <thead>
          <tr>
            <th>Employee Name</th>
            <th>Department</th>
            <th>Monthly Salary</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {employees.map((employee) => (
            <tr key={employee.id}>
              <td>{employee.name}</td>
              <td>{employee.department}</td>
              <td>{employee.salary}</td>
              <td>
                <button onClick={() => handleEdit(employee.id)} className="edit-btn">Edit</button>
                <button onClick={() => handleDelete(employee.id)} className="delete-btn">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ASalary;
