import React, { useState } from "react";
import './ALeave.css';  // Import CSS if needed

function ALeave() {
  const [leaveRequests, setLeaveRequests] = useState([
    { id: 1001, startDate: '2024-05-12', endDate: '2024-05-16', action: '' },
    { id: 1002, startDate: '2024-06-01', endDate: '2024-06-10', action: '' },
    { id: 1003, startDate: '2024-08-15', endDate: '2024-08-22', action: '' },
    { id: 1004, startDate: '2024-09-20', endDate: '2024-09-23', action: '' }
  ]);

  const handleApproval = (id) => {
    setLeaveRequests(leaveRequests.map(request => 
      request.id === id ? { ...request, action: 'Approved' } : request
    ));
  };

  const handleDecline = (id) => {
    setLeaveRequests(leaveRequests.map(request => 
      request.id === id ? { ...request, action: 'Declined' } : request
    ));
  };

  return (
    <div className="leave-requests-container">
      <h2>Leave Requests</h2>
      <table className="leave-requests-table">
        <thead>
          <tr>
            <th>Employee ID</th>
            <th>Start Date</th>
            <th>End Date</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {leaveRequests.map((request) => (
            <tr key={request.id}>
              <td>{request.id}</td>
              <td>{request.startDate}</td>
              <td>{request.endDate}</td>
              <td>
                {request.action === '' ? (
                  <>
                    <button onClick={() => handleApproval(request.id)} className="approve-btn">Approve</button>
                    <button onClick={() => handleDecline(request.id)} className="decline-btn">Decline</button>
                  </>
                ) : (
                  <span>{request.action}</span>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ALeave;
