import React, { useState } from "react";
import Sidebar from "./Sidebar"; // Sidebar component
import Attendance from "./Attendance";
import "./DashBoardApp.css"
import DashBoard from "./Dashboard";

function DashBoardApp() {
  const [activeSection, setActiveSection] = useState("attendance"); // Default to 'attendance'

  // This function is passed to the Sidebar component
  const handleSectionClick = (section) => {
    setActiveSection(section); // Updates the active section based on the clicked section
  };

  return (
    <div className="dashboard-container">
      <Sidebar handleSectionClick={handleSectionClick} /> {/* Pass function to Sidebar */}
      <div className="content-area">
        {/* Conditionally render the corresponding section based on the active section */}
        {activeSection === "employeedashboard" && <DashBoard />}
        {activeSection === "attendance" && <Attendance />}



      </div>
    </div>
  );
}

export default DashBoardApp;
