import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate for routing
import "./LoginForm.css";

function LoginForm() {
  const [userID, setUserID] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate(); // Initialize useNavigate

  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent form from submitting and refreshing the page

    console.log("UserID:", userID);
    console.log("Password:", password);

    // Simple validation for the user credentials
    if (userID === "employee" && password === "password") {
      // Navigate to the employee dashboard after successful login
      navigate("/employeedashboard");
    } else {
      // Alert user if credentials are incorrect
      alert("Invalid UserID or Password");
    }
  };

  return (
    <div className="login-container">
      <h1>Employee Management System</h1>
      <form onSubmit={handleSubmit} className="login-form">
        <div>
          <label htmlFor="email">UserID</label>
          <input
            type="text"
            id="text"
            value={userID}
            onChange={(e) => setUserID(e.target.value)} // Update state as user types
            placeholder="Enter your userID"
          />
        </div>
        <div>
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)} // Update state as user types
            placeholder="Enter your password"
          />
        </div>
        <button type="submit">Sign In</button> {/* User clicks this to submit the form */}
      </form>
    </div>
  );
}

export default LoginForm;
