import React, { useEffect, useState } from "react";
import axios from "axios";
import "./AEmployeeInfo.css";

function AEmployeeInfo() {
  const [employees, setEmployees] = useState([]);
  const [year, setYear] = useState("");

 

  const fetchEmployees = useCallback(async () => {
    try {
      const response = await axios.get(
        `http://localhost:5000/api/employee-info?year=${selectedYear}`
      );
      setEmployees(response.data);
    } catch (error) {
      console.error("Error fetching employee info", error);
    }
  }, [selectedYear]);
  
  useEffect(() => {
    fetchEmployees();
  }, [fetchEmployees]);
  

  return (
    <div className="employee-info-container">
      <h2>Employee Information</h2>
      
      <label>Filter by Year Hired:</label>
      <select onChange={(e) => setYear(e.target.value)} value={year}>
        <option value="">All</option>
        <option value="2020">2020</option>
        <option value="2021">2021</option>
        <option value="2022">2022</option>
        <option value="2023">2023</option>
        <option value="2024">2024</option>
      </select>

      <table className="employee-info-table">
        <thead>
          <tr>
            <th>Employee ID</th>
            <th>Name</th>
            <th>Department</th>
            <th>Hire Date</th>
          </tr>
        </thead>
        <tbody>
          {employees.length > 0 ? (
            employees.map((emp) => (
              <tr key={emp.empID}>
                <td>{emp.empID}</td>
                <td>{emp.empfirstName} {emp.emplastName}</td>
                <td>{emp.deptName}</td>
                <td>{new Date(emp.hireDate).toLocaleDateString()}</td>
              </tr>
            ))
          ) : (
            <tr><td colSpan="4">No employee data found.</td></tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default AEmployeeInfo;
