import React from "react";

function ADepartments() {
  const departments = [
    { DeptID: "S001", DeptName: "HR", dailyPay: "$300" },
    { DeptID:"S002", DeptName: "HR", dailyPay: "$400" },
    {  DeptID:"S003", DeptName: "HR", dailyPay: "$350" },
  ];

  return (
    <div className="departments-container">
      <h2>Manage Departments</h2>
      <button className="add-department-btn">+ Add Department</button>
      <table className="departments-table">
        <thead>
          <tr>
            <th>Department Name</th>
            
           
          </tr>
        </thead>
        <tbody>
          {departments.map((department, index) => (
            <tr key={index}>
              <td>{department.name}</td>
              
              <td>
                <button>Edit</button>
                <button className="delete-btn">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ADepartments;
