import React, { useState } from "react";
import ASideBar  from "./ASideBar"; // Assuming Sidebar is a separate component
import "./AdminApp.css"; // Assuming you will style it here
import ADepartments from "./ADepartments"; // Departments component to manage departments
import AMAttendance from "./AMAttendance";
import "./AttendanceOverview.css";

function AdminApp() {
  const [activeSection, setActiveSection] = useState("departments");

  const handleSectionClick = (section) => {
    setActiveSection(section);
  };

  return (
    <div className="adminapp-container">
      {/* Sidebar Component */}
      <ASideBar handleSectionClick={handleSectionClick} />

      {/* Content Area */}
      <div className="content-area">
        {/* Conditional rendering based on the active section */}
        {activeSection === "departments" && <ADepartments />}
        {activeSection === "attendance" && <AMAttendance />
        }
      </div>
    </div>
  );
}

export default AdminApp;
