import React, { useState, useEffect } from "react";
import './AttendanceOverview.css';

function AMAttendance() {
  const [formOptions, setFormOptions] = useState([]);
  const [selectedEmployee, setSelectedEmployee] = useState("");
  const [selectedDept, setSelectedDept] = useState("");
  const [selectedDate, setSelectedDate] = useState("");
  const [checkInTime, setCheckInTime] = useState("");
  const [attendanceRecords, setAttendanceRecords] = useState([]);
  const [editIndex, setEditIndex] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/attendance/form-options")
      .then(res => res.json())
      .then(data => {
        setFormOptions(data);
        if (data.length > 0) {
          setSelectedEmployee(data[0].name);
          setSelectedDept(data[0].deptName);
        }
      })
      .catch(err => console.error("Error fetching form options", err));
  }, []);

  const handleAddAttendance = () => {
    if (!selectedEmployee || !selectedDept || !selectedDate || !checkInTime) {
      alert("Fill all fields");
      return;
    }

    const newRecord = {
      name: selectedEmployee,
      department: selectedDept,
      date: selectedDate,
      checkIn: checkInTime
    };

    setAttendanceRecords([...attendanceRecords, newRecord]);
    setSelectedDate("");
    setCheckInTime("");
  };

  const handleDelete = (index) => {
    const confirm = window.confirm("Delete this record?");
    if (!confirm) return;
    const updated = [...attendanceRecords];
    updated.splice(index, 1);
    setAttendanceRecords(updated);
  };

  const handleEdit = (index) => {
    setEditIndex(index);
    const record = attendanceRecords[index];
    setSelectedEmployee(record.name);
    setSelectedDept(record.department);
    setSelectedDate(record.date);
    setCheckInTime(record.checkIn);
  };

  const handleSaveEdit = () => {
    const updated = [...attendanceRecords];
    updated[editIndex] = {
      name: selectedEmployee,
      department: selectedDept,
      date: selectedDate,
      checkIn: checkInTime
    };
    setAttendanceRecords(updated);
    setEditIndex(null);
    setSelectedDate("");
    setCheckInTime("");
  };

  return (
    <div className="attendance-container">
      <h2>Attendance Overview</h2>

      <div className="filter-options">
        <div className="filter-item">
          <label>Employee:</label>
          <select value={selectedEmployee} onChange={(e) => setSelectedEmployee(e.target.value)}>
            {formOptions.map((opt, i) => (
              <option key={i} value={opt.name}>{opt.name}</option>
            ))}
          </select>
        </div>

        <div className="filter-item">
          <label>Department:</label>
          <select value={selectedDept} onChange={(e) => setSelectedDept(e.target.value)}>
            {[...new Set(formOptions.map(opt => opt.deptName))].map((dept, i) => (
              <option key={i} value={dept}>{dept}</option>
            ))}
          </select>
        </div>

        <div className="filter-item">
          <label>Date:</label>
          <input type="date" value={selectedDate} onChange={(e) => setSelectedDate(e.target.value)} />
        </div>

        <div className="filter-item">
          <label>Check-In:</label>
          <input type="time" value={checkInTime} onChange={(e) => setCheckInTime(e.target.value)} />
        </div>

        {editIndex === null ? (
          <button className="add-attendance-btn" onClick={handleAddAttendance}>+ Mark Attendance</button>
        ) : (
          <button className="add-attendance-btn" onClick={handleSaveEdit}>Save Edit</button>
        )}
      </div>

      <table className="attendance-table">
        <thead>
          <tr>
            <th>Employee</th>
            <th>Department</th>
            <th>Date</th>
            <th>Check-In</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {attendanceRecords.map((rec, index) => (
            <tr key={index}>
              <td>{rec.name}</td>
              <td>{rec.department}</td>
              <td>{rec.date}</td>
              <td>{rec.checkIn}</td>
              <td>
                <button onClick={() => handleEdit(index)}>Edit</button>
                <button className="delete-btn" onClick={() => handleDelete(index)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AMAttendance;
